services.onAccept:
// we grant analytics storage
if (typeof gtag === 'function'){
  gtag('consent', 'update', {
      'analytics_storage': 'granted',
  });
}

services.onDecline:
// we deny analytics storage
if (typeof gtag === 'function'){
  gtag('consent', 'update', {
      'analytics_storage': 'denied',
  });
}
